public class _214_Shortest_Palindrome {
    public static void main(String[] args) {
        _214_Shortest_Palindrome test = new _214_Shortest_Palindrome();
        System.out.println(test.shortestPalindrome("abc"));
        System.out.println(test.shortestPalindrome("aba"));
        System.out.println(test.shortestPalindrome("a"));
        System.out.println(test.shortestPalindrome("ababd"));
        System.out.println(test.shortestPalindrome("1234"));
        System.out.println(test.shortestPalindrome("1234321"));
        System.out.println(test.shortestPalindrome("1234322"));
        System.out.println(test.shortestPalindrome("abaa"));
        System.out.println(test.shortestPalindrome("aacecaaa"));
        System.out.println(test.shortestPalindrome(""));
    }
    public String shortestPalindrome(String s) {
        if (s == null || s.length() <= 1) {
            return s;
        }
        int left = 0, right = s.length() - 1;
        int count = 0;
        boolean same = true;
        for (; right - left > 0; right--) {
            if (s.charAt(right) == s.charAt(left)) {
                left++;
                if (same) {
                    if (count == 0) {
                        count++;
                    } else {
                        if (s.charAt(right) == s.charAt(right + 1)) {
                            count++;
                        } else {
                            same = false;
                        }
                    }
                }
            }else {
                right += left - count;
                left = 0;
                count = 0;
            }
            count++;
        }
        int copyIndex = left + right;
        if (copyIndex == s.length()) {
            return s;
        } else {
            return new StringBuffer(s.substring(copyIndex + 1)).reverse().toString() + s;
        }
    }
}

